import React, { useState, useEffect } from 'react';

const CustomCursor = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isPointer, setIsPointer] = useState(false);

  useEffect(() => {
    const updatePosition = (e) => {
      setPosition({ x: e.clientX, y: e.clientY });
      
      // Check if hovering over clickable elements
      const element = document.elementFromPoint(e.clientX, e.clientY);
      setIsPointer(
        element?.tagName === 'BUTTON' || 
        element?.tagName === 'A' || 
        getComputedStyle(element || document.body).cursor === 'pointer'
      );
    };

    window.addEventListener('mousemove', updatePosition);
    return () => window.removeEventListener('mousemove', updatePosition);
  }, []);

  return (
    <>
      <div 
        className="fixed w-4 h-4 rounded-full pointer-events-none z-50 mix-blend-difference transition-transform duration-100 bg-white"
        style={{ 
          left: `${position.x}px`, 
          top: `${position.y}px`,
          transform: `translate(-50%, -50%) scale(${isPointer ? 2 : 1})`
        }}
      />
      <div 
        className="fixed w-8 h-8 rounded-full border border-white pointer-events-none z-50 mix-blend-difference"
        style={{ 
          left: `${position.x}px`, 
          top: `${position.y}px`,
          transform: 'translate(-50%, -50%)',
          transition: 'transform 0.3s ease-out, left 0.15s ease-out, top 0.15s ease-out'
        }}
      />
    </>
  );
};

export default CustomCursor;
